(()=>{document.addEventListener("DOMContentLoaded",function(){let e=document.querySelectorAll(".card__content"),n=[];for(let t=0;t<=100;t++)n.push(t/100);e.forEach((t,s)=>{let a=e[s+1];if(!a)return;new IntersectionObserver(i=>{i.forEach(r=>{let o=1-.2*r.intersectionRatio;t.style.transform=`scale(${o})`})},{threshold:n,rootMargin:"0px 0px -30% 0px"}).observe(a)})});})();
